            modelBuilder.Entity<Event>().HasData(
                   new { Id = 1, EventName = "Hiến máu nhân đạo tháng 3", Description = "Ngày hội hiến máu Trái tim tình nguyện: Cho đi giọt máu là cho đi niềm hi vọng sống", Content = "<p style=\"text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); font-family: Sans, Helvetica, tahoma, sans-serif; color: rgb(33, 37, 41); font-size: 16px;\">Được diễn ra trong 2 ngày 3 – 4/12 tại Bảo tàng Hà Nội, ngày hội hiến máu mong muốn truyền đi thông điệp “Chung tay hiến máu cứu người”. Đây đã trở thành nơi gặp gỡ, giao lưu và tôn vinh những người làm công tác tình nguyện.</span></p><p style=\"text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); font-family: Sans, Helvetica, tahoma, sans-serif; color: rgb(33, 37, 41); font-size: 16px;\">Phát biểu tại chương trình, PGS.TS Nguyễn Hà Thanh, Viện trưởng Viện Huyết học – Truyền máu Trung ương cho biết: Ngày hội hiến máu “Trái tim tình nguyện” hôm nay là sự kiện lớn đầu tiên mở đầu cho chuỗi các sự kiện hiến máu lớn dịp trước và sau Tết Nguyên đán 2023. Viện Huyết học – Truyền máu Trung ương đánh giá rất cao ý tưởng tổ chức ngày hội hiến máu “Trái tim tình nguyện” của Hội Thanh niên vận động hiến máu Hà Nội.</span></p><p><br></p>", StartTime = new DateTime(2023, 3, 1), EndTime = new DateTime(2023, 3, 7), Status = EventStatus.Ongoing, ImageId = 10, CreatedAt = DateTime.Now, UpdatedAt = DateTime.Now },
                   new { Id = 2, EventName = "Ngày hội hiến máu “Trái tim tình nguyện”", Description = "Sáng 22/11, tại Hà Nội, Trung ương Hội Chữ thập đỏ Việt Nam tổ chức Ngày hội hiến máu nhân đạo", Content = "<p><strong style=\"background-color: rgb(255, 255, 255); font-family: Sans, Helvetica, tahoma, sans-serif; color: rgb(33, 37, 41); font-size: 16px;\"><em>“Có rất nhiều cách để chia sẻ, cống hiến cho cộng đồng nhưng tôi nghĩ hiến máu là một việc làm rất ý nghĩa, bởi cho đi những giọt máu của mình là cho đi sự sống, cho đi niềm hi vọng đối với một ai đó, điều này vô cùng thiêng liêng và cao cả”</em>, nữ sinh Nguyễn Thị Thu Hiền, trường Cao đẳng Y tế Hà Nội chia sẻ.</strong></p><p><span style=\"background-color: rgb(255, 255, 255); font-family: Sans, Helvetica, tahoma, sans-serif; color: rgb(33, 37, 41); font-size: 16px;\">Hưởng ứng các hoạt động kỷ niệm Ngày Quốc tế tình nguyện 5/12, hôm nay 4/12, Viện Huyết học – Truyền máu Trung ương phối hợp với Trung tâm Tình nguyện Quốc gia, Hội Liên hiệp Thanh niên Việt Nam Thành phố Hà Nội, Hội Thanh niên vận động hiến máu Hà Nội và các đơn vị tổ chức ngày hội hiến máu “Trái tim tình nguyện” năm 2022.</span></p><p><span style=\"background-color: rgb(255, 255, 255); font-family: Sans, Helvetica, tahoma, sans-serif; color: rgb(33, 37, 41); font-size: 16px;\">Tham dự chương trình có PGS.TS Nguyễn Hà Thanh, Viện trưởng Viện Huyết học – Truyền máu Trung ương; TS Trần Ngọc Quế, Giám đốc Trung tâm Máu Quốc gia, Viện Huyết học – Truyền máu Trung ương; Bà Đỗ Thị Kim Hoa, Giám đốc Trung tâm Tình nguyện Quốc gia; Ông Nguyễn Đức Tiến, Phó Bí thư Thường trực Thành đoàn, Chủ tịch Hội Liên hiệp Thanh niên Việt Nam Thành phố Hà Nội cùng đại diện các cơ quan đơn vị liên quan.</span></p>", StartTime = new DateTime(2023, 3, 7), EndTime = new DateTime(2023, 3, 14), Status = EventStatus.Finished, ImageId = 11, CreatedAt = DateTime.Now, UpdatedAt = DateTime.Now },
                   new { Id = 3, EventName = "Ngày hội hiến máu nhân đạo", Description = "Mỗi giọt máu hiến tặng góp phần thể hiện tinh thần tương thân, tương ái trong cộng đồng", Content = "<p style=\"text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); font-family: Sans, Helvetica, tahoma, sans-serif; color: rgb(33, 37, 41); font-size: 16px;\">Được diễn ra trong 2 ngày 3 – 4/12 tại Bảo tàng Hà Nội, ngày hội hiến máu mong muốn truyền đi thông điệp “Chung tay hiến máu cứu người”. Đây đã trở thành nơi gặp gỡ, giao lưu và tôn vinh những người làm công tác tình nguyện.</span></p><p style=\"text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); font-family: Sans, Helvetica, tahoma, sans-serif; color: rgb(33, 37, 41); font-size: 16px;\">Phát biểu tại chương trình, PGS.TS Nguyễn Hà Thanh, Viện trưởng Viện Huyết học – Truyền máu Trung ương cho biết: Ngày hội hiến máu “Trái tim tình nguyện” hôm nay là sự kiện lớn đầu tiên mở đầu cho chuỗi các sự kiện hiến máu lớn dịp trước và sau Tết Nguyên đán 2023. Viện Huyết học – Truyền máu Trung ương đánh giá rất cao ý tưởng tổ chức ngày hội hiến máu “Trái tim tình nguyện” của Hội Thanh niên vận động hiến máu Hà Nội.</span></p><p><br></p>", StartTime = new DateTime(2023, 3, 8), EndTime = new DateTime(2023, 3, 19), Status = EventStatus.Finished, ImageId = 12, CreatedAt = DateTime.Now, UpdatedAt = DateTime.Now },
                   new { Id = 4, EventName = "Ngày hội hiến máu tình nguyện tháng 4", Description = "Ngày hội hiến máu Trái tim tình nguyện: Cho đi giọt máu là cho đi niềm hi vọng sống", Content = "<p><span style=\"background-color: rgb(255, 255, 255); font-size: 14px; font-family: Roboto, Arial, Helvetica, sans-serif; color: rgb(0, 0, 0);\">Máu rất cần thiết cho việc cấp cứu ngoại khoa, cấp cứu các trường hợp tai nạn giao thông, dùng điều trị đối với các bệnh về máu, cấp cứu nạn nhân trong các thảm họa, thiên tai…. Thông qua việc hiến máu giúp cho người bệnh cần máu điều trị tại các bệnh viện, nhiều người đã được cứu sống trở về với gia đình, người thân, bạn bè. Hiến máu cứu người là nghĩa cử cao đẹp, là nghĩa vụ thiêng liêng và trách nhiệm của mỗi người, thể hiện giá trị đạo đức tinh thần của người hiến máu “Mình vì mọi người”, “Tương thân tương ái” và thực hiện theo lời căn dặn của Chủ tịch Hồ Chí Minh “ Phải xuất phát từ tình thương yêu Nhân dân tha thiết mà góp phần bảo vệ sức khỏe của Nhân dân và làm mọi việc có thể làm được để giảm bớt đau thương cho họ” Với ý nghĩa đó: Để Ngày hội hiến máu diễn ra an toàn, hiệu quả, Ban Chỉ đạo vận động HMTN huyện Ngọc Lặc đã tích cực đẩy mạnh công tác vận động, tuyên truyền về hiến máu nhân đạo bằng nhiều hình thức; đồng thời, triển khai Kế hoạch tổ chức hiến máu tình nguyện đến từng xã, thị trấn, cơ quan, đơn vị trên địa bàn huyện.</span></p>", StartTime = new DateTime(2023, 3, 20), EndTime = new DateTime(2023, 3, 30), Status = EventStatus.Ongoing, ImageId = 13, CreatedAt = DateTime.Now, UpdatedAt = DateTime.Now },
                   new { Id = 5, EventName = "Gửi trao nguồn sống, viết tiếp thanh xuân", Description = "Có thể nói, năm học 2018-2019 là năm học thành công của Đội tuyên truyền hiến máu tình nguyện", Content = "<p><strong style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0); font-size: 14px; font-family: inherit;\">Hơn 1000 đơn vị máu được hiến tặng chỉ trong 2 buổi sáng Ngày hội hiến máu tình nguyện “GỬI TRAO NGUỒN SỐNG, VIẾT TIẾP THANH XUÂN</strong><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0); font-size: 14px; font-family: Arial;\">”</span><strong style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0); font-size: 14px; font-family: inherit;\">tại trường Đại học Mở TP. HCM diễn ra vào ngày 29 và 30 tháng 6.</strong></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0); font-size: 14px; font-family: Arial;\">Vào sáng ngày 29 và 30 tháng 6/2019 tại trường Đại học Mở Thành phố Hồ Chí Minh, 371 Nguyễn Kiệm, Gò Vấp đã diễn Ngày hội hiến máu tình nguyện “GỬI TRAO NGUỒN SỐNG, VIẾT TIẾP THANH XUÂN”, do Đội tuyên truyền hiến máu tình nguyện Đại học Mở Thành phố Hồ Chí Minh kết hợp cùng Bệnh viên Truyền Máu Huyết Học tổ chức. Qua 2 ngày hội, sự kiện trên đã thu hút 715 người tham gia hiến máu với hơn 1000 đơn vị máu được bệnh viện tiếp nhận.</span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0); font-size: 14px; font-family: Arial;\">Ngay từ sáng sớm đã có nhiều bạn sinh viên đến để được hiến máu. Không chỉ có các bạn sinh viên trường Đại học Mở mà còn có các anh chị cựu sinh viên, cán bộ công nhân viên nhà trường và cả những bạn sinh viên trường khác cũng đến tham dự.</span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0); font-size: 14px; font-family: Arial;\">Không khí ngày hội luôn vui tươi và đầy sức sống cho đến tận lúc kết thúc. Từ người đi hiến, các y bác sĩ đến các thành viên đội Máu hỗ trợ trong ngày hội, ai cũng giữ nụ cười tươi tắn trên môi.</span></p><p><br></p>", StartTime = new DateTime(2023, 4, 1), EndTime = new DateTime(2023, 4,4), Status = EventStatus.Upcoming, ImageId = 14, CreatedAt = DateTime.Now, UpdatedAt = DateTime.Now }
             );
